sys_variable_value.value:
(function execute(inputs, outputs) {

  var body = inputs.body;

  var xml = new XMLDocument2();
  xml.parseXML(body);

  var schedule = {};

  var startDate = new GlideDate();
  var date = xml.getNodeText('//StartDate');
  var dateToSet = date.split('T');
  startDate.setValue(dateToSet[0]);

  schedule.startDate = startDate.getValue();

  var week1 = {};
  week1.monday = parseInt(xml.getNodeText('//HoursMonday'));
  week1.tuesday = parseInt(xml.getNodeText('//HoursTuesday'));
  week1.wednesday = parseInt(xml.getNodeText('//HoursWednesday'));
  week1.thursday = parseInt(xml.getNodeText('//HoursThursday'));
  week1.friday = parseInt(xml.getNodeText('//HoursFriday'));
  week1.saturday = parseInt(xml.getNodeText('//HoursSaturday'));
  week1.sunday = parseInt(xml.getNodeText('//HoursSunday'));

  var week2 = {};
  week2.monday = parseInt(xml.getNodeText('//HoursMonday2'));
  week2.tuesday = parseInt(xml.getNodeText('//HoursTuesday2'));
  week2.wednesday = parseInt(xml.getNodeText('//HoursWednesday2'));
  week2.thursday = parseInt(xml.getNodeText('//HoursThursday2'));
  week2.friday = parseInt(xml.getNodeText('//HoursFriday2'));
  week2.saturday = parseInt(xml.getNodeText('//HoursSaturday2'));
  week2.sunday = parseInt(xml.getNodeText('//HoursSunday2'));

  schedule.week1 = week1;
  schedule.week2 = week2;

  outputs.schedule = schedule;
  outputs.schedulestring = JSON.stringify(schedule);

})(inputs, outputs);

